/* eslint-disable react/prop-types */
import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { useNavigate } from 'react-router-dom';

function ThreadInput({ addSomeThread }) {
  const [title, onTitleChange] = useState('');
  const [body, onBodyChange] = useState('');
  const [category, onCategoryChange] = useState('');

  const navigate = useNavigate();

  function threadInputHandler() {
    if (title.trim() && body.trim() && category.trim()) {
      addSomeThread(title, body, category);
      navigate('/');
    }
  }

  return (
    <div>
      <input type="text" value={title} placeholder="Title" onChange={onTitleChange} />
      <input type="text" value={category} placeholder="Category" onChange={onCategoryChange} />
      <textarea type="text" placeholder="Add some text." value={body} onChange={onBodyChange} />
      <button type="submit" onClick={threadInputHandler}>Post</button>
    </div>
  );
}

ThreadInput.propTypes = {
  addSomeThread: PropTypes.func.isRequired,
};

export default ThreadInput;
