/* eslint-disable react/prop-types */
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function ThreadInput({ addSomeThread }) {
  const [title, setTitle] = useState('');
  const [body, setBody] = useState('');
  const [category, setCategory] = useState('');

  const navigate = useNavigate();

  function threadInputHandler() {
    if (title.trim() && body.trim() && category.trim()) {
      addSomeThread(title, body, category);
      navigate('/');
    }
  }

    return (
      <div>
        <input type="text" value={title} placeholder="Title" onChange={setTitle} />
        <input type="text" value={category} placeholder="Category" onChange={setBody} />
        <textarea type="text" placeholder="Add some text." value={body} onChange={setCategory} />
        <button type="submit" onClick={threadInputHandler}>Post</button>
      </div>
    );
  
}

export default ThreadInput;
