/* eslint-disable no-undef */
import React from 'react';
import PropTypes from 'prop-types';
import parse from 'html-react-parser';
import { AiOutlineLike, AiOutlineDislike } from 'react-icons/ai';
import { postedAt } from '../utils';

function ThreadDetail({
  id,
  title,
  body,
  category,
  createdAt,
  owner,
  upVotesBy,
  downVotesBy,
//   comments,
  authUser,
  like,
  dislike,
  neutralLike,
  neutralDislike,
  addCommentThread,
}) {
  const isThreadLiked = upVotesBy.includes(authUser);
  const isThreadDisliked = downVotesBy.includes(authUser);

  const onLikeClick = (event) => {
    event.stopPropagation();
    if (!isThreadLiked && !isThreadDisliked) {
      like(id);
    } else if (isThreadDisliked) {
      neutralDislike(id);
      like(id);
    } else if (isThreadLiked) {
      neutralLike(id);
    }
  };

  const onDislikeClick = (event) => {
    event.stopPropagation();
    if (!isThreadLiked && !isThreadDisliked) {
      dislike(id);
    } else if (isThreadLiked) {
      neutralLike(id);
      dislike(id);
    } else if (isThreadDisliked) {
      neutralDislike(id);
    }
  };

  return (
    <section>
      <div>
        <div>
          <img src={owner.avatar} alt={owner.name} />
          <div>
            <h3>{owner.name}</h3>
            <p>{`Created ${postedAt(createdAt)}`}</p>
          </div>
        </div>
      </div>
      <p>{`# ${category}`}</p>
      <div>
        <h2>{title}</h2>
        <p>{parse(body)}</p>
      </div>
      <div className="thread-item__action">
        <p className="action__list">
          <button type="button" onClick={onLikeClick} className="action__like-button">
            {isThreadLiked ? (
              <AiOutlineLike style={{ color: 'blue' }} />
            ) : (
              <AiOutlineLike />
            )}
          </button>
          {isThreadLiked ? (
            <span style={{ color: 'blue' }}>{upVotesBy.length}</span>
          ) : (
            <span>{upVotesBy.length}</span>
          )}
        </p>

        <p className="action__list">
          <button type="button" onClick={onDislikeClick} className="action__dislike-button">
            {isThreadDisliked ? (
              <AiOutlineDislike style={{ color: 'red' }} />
            ) : (
              <AiOutlineDislike />
            )}
          </button>
          {isThreadDisliked ? (
            <span style={{ color: 'red' }}>{downVotesBy.length}</span>
          ) : (
            <span>{downVotesBy.length}</span>
          )}
        </p>
      </div>

    </section>
  );
}

const userShape = {
  id: PropTypes.string.isRequired,
  name: PropTypes.string.isRequired,
  avatar: PropTypes.string.isRequired,
};

ThreadDetail.propTypes = {
  id: PropTypes.string.isRequired,
  title: PropTypes.string.isRequired,
  body: PropTypes.string.isRequired,
  category: PropTypes.string.isRequired,
  createdAt: PropTypes.string.isRequired,
  owner: PropTypes.shape(userShape).isRequired,
  upVotesBy: PropTypes.arrayOf(PropTypes.string).isRequired,
  downVotesBy: PropTypes.arrayOf(PropTypes.string).isRequired,
//   comments: PropTypes.arrayOf(PropTypes.shape(threadCommentItemShape)).isRequired,
  authUser: PropTypes.string.isRequired,
  like: PropTypes.func,
  dislike: PropTypes.func,
  neutralLike: PropTypes.func,
  neutralDislike: PropTypes.func,
  addCommentThread: PropTypes.func,
};

ThreadDetail.defaultProps = {
  like: null,
  dislike: null,
  neutralLike: null,
  neutralDislike: null,
  addCommentThread: null,
};

export { userShape };

export default ThreadDetail;
